package String_;

public class last_index {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s = "Maharshtra";
		String t = "Tamilnadu";
		System.out.println(s.lastIndexOf("a"));
		System.out.println(t.lastIndexOf("a",2));
		System.out.println(s.lastIndexOf("ra"));

	}

}

